  const reactions = [
    { emoji: 'â¤ï¸', key: 'love', label: 'Love it!' },
    { emoji: 'ðŸ‘', key: 'like', label: 'Like it' },
    { emoji: 'ðŸ¤”', key: 'think', label: 'Thinking...' },
    { emoji: 'ðŸ˜®', key: 'wow', label: 'Amazing!' },
    { emoji: 'â“', key: 'question', label: 'Question' }
  ];

  const handleReaction = (key) => {
    if (selectedReaction === key) return;

    if (selectedReaction) {
      setCounts(prev => ({
        ...prev,
        [selectedReaction]: prev[selectedReaction] - 1,
        [key]: prev[key] + 1
