{
  "type": "CustomComponent",
  "props": {
    "position": {"x": 560, "y": 400},
    "width": 800,
    "height": 200,
    "render": "function render({ props }, instanceId) {
      const text = props.mainText || 'Animated Text';
      const fontSize = props.fontSize || 48;
      const textColor = props.textColor || '#FFFFFF';
      const bgColor = props.bgColor || '#000000';
      const borderRadius = props.borderRadius || 20;

      return React.createElement('div', {
        style: {
          width: '100%',
          height: '100%',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          backgroundColor: bgColor,
          borderRadius: borderRadius + 'px',
          fontSize: fontSize + 'px',
          color: textColor,
          padding: '40px',
          boxSizing: 'border-box'
        }
      }, text);
    }",
    "props": {
      "mainText": "Animated Text Here",
      "fontSize": 48,
      "textColor": "#FFFFFF",
      "bgColor": "#000000",
      "borderRadius": 20
    }
  }
}
